mapper.update:


    UPDATE COM.CSYS110                                			        
       SET                                 
         CMMN_DETA_CD               = #{CMMN_DETA_CD}
         , CMMN_DETA_CD_NM          = #{CMMN_DETA_CD_NM}
         , CMMN_DETA_CD_ABBNM       = #{CMMN_DETA_CD_ABBNM}
         , USE_YN                   = #{USE_YN}
         , SORT_SEQ	                = #{SORT_SEQ}
         , FRST_INPUT_ID            = #{FRST_INPUT_ID}              /*등록자*/  
         , FRST_INPUT_DT            = SYSDATE                       /*등록일시*/  
         , LAST_MODF_ID             = #{LAST_MODF_ID}               /*수정자*/  
         , LAST_MODF_DT             = SYSDATE                       /*수정일시*/  
     WHERE 1=1                                                   						                        
       AND CMMN_CD        = #{CMMN_CD}
       AND CMMN_DETA_CD   = #{CMMN_DETA_CD}  

/* CsyscdDAO.updateCommCodeDetailList 상세코드 수정 */



    UPDATE COM.CSYS111                                			        
       SET                                                      				
         CMMN_DETA_CD               = #{CMMN_DETA_CD}
         , LANG_FG_CD               = #{LANG_FG_CD}
         , LANG_FG_NM               = #{LANG_FG_NM}
         , LANG_ABBNM               = #{LANG_ABBNM}
         , USE_YN                   = #{USE_YN}
         , REMK_CTNT                = #{REMK_CTNT}
         , FRST_INPUT_ID            = #{FRST_INPUT_ID}              /*등록자*/  
         , FRST_INPUT_DT            = SYSDATE                       /*등록일시*/  
         , LAST_MODF_ID             = #{LAST_MODF_ID}               /*수정자*/  
         , LAST_MODF_DT             = SYSDATE                       /*수정일시*/  
     WHERE 1=1                                                   						                        
       AND CMMN_CD        = #{CMMN_CD}
       AND CMMN_DETA_CD   = #{CMMN_DETA_CD}
       AND LANG_FG_CD     = #{LANG_FG_CD}  

/* CsyscdDAO.updateCommCodeDetailList2 언어코드 수정 */

mapper.delete:


    DELETE FROM COM.CSYS100                                                  							      
     WHERE 1=1                              																	          
       AND CMMN_CD        = #{CMMN_CD}  

/* CsyscdDAO.deleteCommCodeMasterList 공통코드 삭제 */



    DELETE FROM COM.CSYS110                                                  							      
     WHERE 1=1                              																	          
       AND CMMN_CD        = #{CMMN_CD}  

/* SYS005CommCodeDetailDAO.deleteCommCodeAllList 상세코드 전체삭제*/



    DELETE FROM COM.CSYS110                                                 							      
     WHERE 1=1                              																	          
       AND CMMN_CD        = #{CMMN_CD}  
       AND CMMN_DETA_CD   = #{CMMN_DETA_CD}  

/* SYS005CommCodeDetailDAO.deleteCommCodeDetailList 상세코드 삭제 */



    DELETE FROM COM.CSYS111                                                  							      
     WHERE 1=1                              																	          
       AND CMMN_CD        = #{CMMN_CD}  

/* SYS005CommCodeDetailDAO.deleteCommCodeAllList 상세코드 전체삭제*/



    DELETE FROM COM.CSYS111                                                 							      
     WHERE 1=1                              																	          
       AND CMMN_CD        = #{CMMN_CD}  
       AND CMMN_DETA_CD   = #{CMMN_DETA_CD}  

/* SYS005CommCodeDetailDAO.deleteCommCodeDetailList 언어코드 삭제 */



