     WHERE 1=1                              																	          
       AND CMMN_CD        = #{CMMN_CD}  
       AND CMMN_GRP_CD   = #{CMMN_GRP_CD}  

/* CsyscdDAO.deleteGrpCodeList 공통 코드 삭제 */



