/* CsyscdDAO.deleteGrpCodeDetailList 공통 상세 코드 삭제 */

mapper.select:
SELECT '0' as CHK, A.DEPT_CD, A.UP_DEPT_CD, A.DEPT_GRP_FG_CD, A.DEPT_TYP_CD, A.UNIV_CD, A.SUST_CD, A.DEPT_NM, A.DEPT_ABBNM, A.SHAF_FORMA_YN, A.DAN_FG, A.FR_DT, A.ABLN_DT, A.ABLN_EXPC_DT, A.PRN_ORD, A.CYBER_FORMA_YN, A.USE_YN, A.FL_DEPT_YN, A.REMK, A.CAMP_FG_CD, A.FRST_INPUT_ID, A.FRST_INPUT_DT, A.FRST_INPUT_MENU_ID, A.FRST_INPUT_IP, A.LAST_MODF_ID, A.LAST_MODF_DT, A.LAST_MODF_MENU_ID, A.LAST_MODF_IP, BREU_FG, A.FORMA_TYP, A.DEPT_ENG_NM, A.DEPT_ENG_ABB_NM, A.DEPT_CHN_NM, A.DEPT_CHN_ABB_NM, A.DEPT_ID, A.COLG_NM, A.SUST_TEAM_NM, A.CERT_DEPT_NM, A.DAYTM_SUST_CD, A.INFO_ANNC_PART, A.SHAF_DEGR, A.MASTER_DEGR, A.PHD_DEGR, MJ_CLSF, A.CONN_MJ, ACHV_PART, A.REG_PART, A.GRDT_DEGR_IDCD_PRN_NM, A.LSN_YR, A.ACHV_SCRN_UNIV, A.GRSC_CLSF, A.SUST_NM, A.GW_DEPT_CONN_YN, A.SPRF_GW_DEPT_CD, A.EMP_GW_DEPT_CD, A.DEPT_SORT_SEQ, A.SUB_CHANC_MISS_CD FROM COM.CSYS300 A WHERE 1 = 1
AND A.DEPT_NM LIKE '%' || #{DEPT_NM} || '%'
AND A.DEPT_CD LIKE '%' || #{DEPT_CD} || '%'
AND A.DEPT_ID LIKE '%' || #{DEPT_ID} || '%'
AND A.USE_YN = #{USE_YN}
AND A.BREU_FG = #{BREU_FG}
AND A.FL_DEPT_YN = #{FL_DEPT_YN}
AND A.SHAF_FORMA_YN = #{SHAF_FORMA_YN}
ORDER BY A.DEPT_CD
