mapper.select:
SELECT '0' AS CHK /*CHK*/ , A.CMMN_CD /*공통코드*/ , A.CMMN_CD_NM /*공통코드명*/ , A.USE_YN /*사용여부*/ , A.REMK_CTNT /*비고내역*/ , A.BF_CMMN_CD /*이전공통코드*/ , A.FRST_INPUT_ID /*최초입력자ID*/ , A.FRST_INPUT_DT /*최초입력일시*/ , A.FRST_INPUT_MENU_ID /*최초입력메뉴ID*/ , A.FRST_INPUT_IP /*최초입력IP*/ , A.LAST_MODF_ID /*최종수정입력자ID*/ , A.LAST_MODF_DT /*최종수정일시*/ , A.LAST_MODF_MENU_ID /*최종수정메뉴ID*/ , A.LAST_MODF_IP /*최종수정IP*/ FROM COM.CSYS100 A WHERE 1=1
AND A.CMMN_CD = #{CMMN_CD}
AND A.CMMN_CD_NM LIKE '%' || #{CMMN_CD_NM} || '%'
AND A.USE_YN LIKE '%' || #{USE_YN} || '%'
AND A.BF_CMMN_CD LIKE '%' || #{BF_CMMN_CD} || '%'
/* CsyscdDAO.retrieveCommCodeMasterList 공통코드 조회 */

SELECT 
	CMMN_CD
	, CMMN_DETA_CD
FROM COM.CSYS110 
WHERE CMMN_CD = #{CMMN_CD}
AND CMMN_DETA_CD = #{CMMN_DETA_CD}
/* CsyscdDAO.retrieveCommCodeDetailList 상세코드 조회 */

mapper.insert:


    INSERT INTO COM.CSYS100                                       										  
    (                                       																	  
           CMMN_CD                    /*공통코드*/ 
         , CMMN_CD_NM                 /*공통코드명*/ 
         , USE_YN                     /*사용여부*/ 
         , REMK_CTNT 			      /*비고내역*/
         , BF_CMMN_CD                 /*이전공통코드*/
         , FRST_INPUT_ID              /*등록자*/ 
         , FRST_INPUT_DT              /*등록일시*/ 
         , LAST_MODF_ID               /*수정자*/ 
         , LAST_MODF_DT               /*수정일시*/ 

    )VALUES                                                  														
    (                                                        														
           #{CMMN_CD} 
         , #{CMMN_CD_NM} 
         , #{USE_YN} 
         , #{REMK_CTNT}  
         , #{BF_CMMN_CD} 
         , #{FRST_INPUT_ID}  
         , SYSDATE                                   
         , #{LAST_MODF_ID}  
         , SYSDATE                                   

    )	                                                      						

/* CsyscdDAO.insertCommCodeMasterList 공통코드 저장 */

INSERT INTO COM.CSYS110 ( CMMN_CD /*공통코드*/ , CMMN_DETA_CD /*공통상세코드*/ , CMMN_DETA_CD_NM /*공통상세코드명*/ , CMMN_DETA_CD_ABBNM /*공통상세코드약명*/ , USE_YN /*사용여부*/ , SORT_SEQ /*정렬순번*/ , FRST_INPUT_ID /*등록자*/ , FRST_INPUT_DT /*등록일시*/ , LAST_MODF_ID /*수정자*/ , LAST_MODF_DT /*수정일시*/
)VALUES( #{CMMN_CD} , #{CMMN_DETA_CD} , #{CMMN_DETA_CD_NM} , #{CMMN_DETA_CD_ABBNM} , #{USE_YN} , #{SORT_SEQ} , #{FRST_INPUT_ID} , SYSDATE , #{LAST_MODF_ID} , SYSDATE )
/* CsyscdDAO.insertCommCodeDetailList 상세코드 저장 */



    INSERT INTO COM.CSYS111                                       										  
    (                                       																	  
         CMMN_CD                      /*공통코드*/
         , CMMN_DETA_CD               /*공통상세코드*/
         , LANG_FG_CD                 /*언어구분코드*/
         , LANG_FG_NM                 /*언어코드명*/
         , LANG_ABBNM                 /*언어코드약명*/
         , USE_YN                     /*사용여부*/
         , REMK_CTNT                  /*비고내역*/
         , FRST_INPUT_ID              /*등록자*/ 
         , FRST_INPUT_DT              /*등록일시*/ 
         , LAST_MODF_ID               /*수정자*/ 
         , LAST_MODF_DT               /*수정일시*/ 

    )VALUES                                                  														
    (                                                        														
           #{CMMN_CD} 
         , #{CMMN_DETA_CD} 
         , #{LANG_FG_CD}  
         , #{LANG_FG_NM} 
         , #{LANG_ABBNM}
         , #{USE_YN}
         , #{REMK_CTNT}  
         , #{FRST_INPUT_ID}  
         , SYSDATE                                   
         , #{LAST_MODF_ID}  
         , SYSDATE                                   

    )	                                                      						

/* CsyscdDAO.insertCommCodeDetailList2 언어코드 저장 */

mapper.update:


    UPDATE COM.CSYS110                                			        
       SET                                 
         CMMN_DETA_CD               = #{CMMN_DETA_CD}
         , CMMN_DETA_CD_NM          = #{CMMN_DETA_CD_NM}
         , CMMN_DETA_CD_ABBNM       = #{CMMN_DETA_CD_ABBNM}
         , USE_YN                   = #{USE_YN}
         , SORT_SEQ	                = #{SORT_SEQ}
         , FRST_INPUT_ID            = #{FRST_INPUT_ID}              /*등록자*/  
         , FRST_INPUT_DT            = SYSDATE                       /*등록일시*/  
         , LAST_MODF_ID             = #{LAST_MODF_ID}               /*수정자*/  
         , LAST_MODF_DT             = SYSDATE                       /*수정일시*/  
     WHERE 1=1                                                   						                        
       AND CMMN_CD        = #{CMMN_CD}
       AND CMMN_DETA_CD   = #{CMMN_DETA_CD}  

/* CsyscdDAO.updateCommCodeMasterList 상세코드 수정 */



    UPDATE COM.CSYS111                                			        
       SET                                                      				
         CMMN_DETA_CD               = #{CMMN_DETA_CD}
         , LANG_FG_CD               = #{LANG_FG_CD}
         , LANG_FG_NM               = #{LANG_FG_NM}
         , LANG_ABBNM               = #{LANG_ABBNM}
         , USE_YN                   = #{USE_YN}
         , REMK_CTNT                = #{REMK_CTNT}
         , FRST_INPUT_ID            = #{FRST_INPUT_ID}              /*등록자*/  
         , FRST_INPUT_DT            = SYSDATE                       /*등록일시*/  
         , LAST_MODF_ID             = #{LAST_MODF_ID}               /*수정자*/  
         , LAST_MODF_DT             = SYSDATE                       /*수정일시*/  
     WHERE 1=1                                                   						                        
       AND CMMN_CD        = #{CMMN_CD}
       AND CMMN_DETA_CD   = #{CMMN_DETA_CD}
       AND LANG_FG_CD     = #{LANG_FG_CD}  

/* CsyscdDAO.updateCommCodeMasterList 언어코드 수정 */

mapper.delete:


    DELETE FROM COM.CSYS100                                                  							      
     WHERE 1=1                              																	          
       AND CMMN_CD        = #{CMMN_CD}  

/* CsyscdDAO.deleteCommCodeMasterList 공통코드 삭제 */



    DELETE FROM COM.CSYS110                                                  							      
     WHERE 1=1                              																	          
       AND CMMN_CD        = #{CMMN_CD}  

/* SYS005CommCodeDetailDAO.deleteCommCodeAllList 상세코드 전체삭제*/



    DELETE FROM COM.CSYS110                                                 							      
     WHERE 1=1                              																	          
       AND CMMN_CD        = #{CMMN_CD}  
       AND CMMN_DETA_CD   = #{CMMN_DETA_CD}  

/* SYS005CommCodeDetailDAO.deleteCommCodeDetailList 상세코드 삭제 */



    DELETE FROM COM.CSYS111                                                  							      
     WHERE 1=1                              																	          
       AND CMMN_CD        = #{CMMN_CD}  

/* SYS005CommCodeDetailDAO.deleteCommCodeAllList 상세코드 전체삭제*/



DELETE FROM COM.CSYS111                                                 							      
 WHERE 1=1                              																	          
   AND CMMN_CD        = #{CMMN_CD}  
