mapper.select:
/* SYS001ProgramMasterDAO.retrieveProgramMasterList 프로그램 마스터 조회 */ SELECT '0' AS CHK /* CHEK */ , A.PGM_ID AS PROG_ID /* 프로그램ID */ , A.PGM_PATH_NM /* 프로그램경로명 */ , A.PGM_NM /* 프로그램명 */ , A.PGM_KND_FG_CD /* 프로그램종류구분코드 */ , A.SYSTEM_FG_CD /* 시스템구분코드 */ , A.BUSS_LCLAS_CD /* 업무대분류코드 */ , A.BUSS_MCLSF_CD /* 업무중분류코드 */ , A.USE_YN /* 사용여부 */ , A.SORT_SEQ /* 정렬순번 */ , A.FRST_INPUT_ID /* 최초입력자ID */ , A.FRST_INPUT_DT /* 최초입력일시 */ , A.LAST_MODF_ID /* 최종수정입력자ID */ , A.LAST_MODF_DT /* 최종수정일시 */ FROM COM.CSYS200 A WHERE 1 = 1
AND A.PGM_ID LIKE '%' || #{PROG_ID} || '%'
AND A.PGM_NM LIKE '%' || #{PGM_NM} || '%'
AND A.USE_YN = #{USER_YN}
ORDER BY A.PGM_ID

	/* SYS001ProgramMasterDAO.retrieveProgramMasterListPKey 프로그램 키값을 추출 조회 */                    									
	SELECT 'P' || LPAD(TO_NUMBER(SUBSTR(MAX(A.PGM_ID), 2, 7)) + 1, 6, 0) AS KEY_CODE
      FROM COM.CSYS200 A

	/* SYS001ProgramMasterDAO.retrieveProgramMasterListKey 프로그램 중복 체크 */
	SELECT COUNT(1) AS CNT
      FROM COM.CSYS200 A
     WHERE A.PGM_ID = #{PROG_ID}



     SELECT
            COUNT(1) AS CNT
       FROM COMM.XBT_PROG_MAST
      WHERE 1=1
        AND PROG_ID = #{PROG_ID} 

/* SYS001ProgramMasterDAO.retrieveProgramMasterListKey 프로그램 중복 체크 */

mapper.delete:
/* SYS001ProgramMasterDAO.deleteProgramMasterList 프로그램 삭제 */
DELETE FROM COM.CSYS200
 WHERE 1 = 1
   AND PGM_ID = #{PROG_ID}
